import { io } from "socket.io-client";

const URL = "http://localhost:3000";
// const socket = 


let socket;

module.exports = {
    init:(userID)=>{
        socket = io(URL, { autoConnect: false });
        socket.auth = {userId:userID};
        return socket;
    },
    getSocket:()=>{
        if(!socket){
            throw new Error('Socket not initialized');
        }
        return socket;
    }
}
